import React, { useState } from "react";
import './App.css';

function App() {

  const [query, setQuery] = useState();
  const [results, setResults] = useState([]);
  const [yksi, setYksi] = useState([]);

  const ClickHandler = (event, props) => {
    event.preventDefault();
    console.log("Toimii");
    console.log(query);
    HaeYksiLeffa();
  };

  const HaeYksiLeffa = () => {
    console.log("Haetaan elokuva id:n mukaan...");
    fetch("https://fullstack-project2.onrender.com/api/hae/"+query)
    .then((result) => {
      return result.json();
    })
    .then((data) => {
      console.log(data);
      setYksi(data)
    });
  };

//CORS pitää olla selaimessa sallittu, muuten ei toimi.
  const HaeLeffaData = () => {
    console.log("Haetaan elokuvia...");
    fetch("https://fullstack-project2.onrender.com/api/leffat")
      .then((results) => {
        return results.json();
      })
      .then((data) => {
        console.log(data);
        setResults(data)
      });
  };

  const LeffaArray = (props) => {
     return (
      <>
        <p>{props.title}</p>
        <img alt="juliste" src={props.poster}/>
      </>
     )
  }

  const YksiLeffa = (data) => {
    return (
      <>
        <p>{data.title}</p>
        <img alt="juliste" src={data.poster}/>
      </>
    )

  }


  return (
    <div>
      <h1>Testing...</h1>
      <p>More testing....</p>
      <button type="button" className="btn" onClick={HaeLeffaData}>
        Hae kaikki leffat
      </button>
      <form onSubmit={ClickHandler}>
        <label>Hae elokuva id:llä</label>
        <input value={query} type="text" onChange={(event) => setQuery(event.target.value)}/>
        <button type="submit">
          Lähetä
        </button>
      </form>
      {
        results ?
          results.map((data, index) => <LeffaArray title={data.title} poster={data.poster} />)
          : <YksiLeffa />
        }
        {yksi.map((data, index) => <YksiLeffa title={data.title} poster={data.poster} />)}
    </div>
  );
}

export default App;
