import React, { useState } from "react";
import './App.css';

function App() {

  const [query, setQuery] = useState();
  const [results, setResults] = useState([]);
  const [yksi, setYksi] = useState([]);
  const [vuosi, setVuosi] = useState();
  const [nimi, setNimi] = useState();

  const ClickHandler = (event, props) => {
    event.preventDefault();
    console.log("Toimii");
    console.log(query);
    HaeYksiLeffa();
  };

  const HaeYksiLeffa = () => {
    console.log("Haetaan elokuva id:n mukaan...");
    //Tyhjennetään edelliset tulokset, jotta uudet mahtuvat tilalle.
    setResults(['']);
    fetch("https://fullstack-project2.onrender.com/api/hae/"+query)
    .then((result) => {
      return result.json();
    })
    .then((data) => {
      console.log(data);
      setYksi(data)
      YksiLeffa(data);
    });
  };

//CORS pitää olla selaimessa sallittu, muuten ei toimi.
  const HaeLeffaData = () => {
    //Tyhjennetään edelliset tulokset, jotta uudet mahtuvat tilalle.
    setYksi(['']);
    console.log("Haetaan elokuvia...");
    fetch("https://fullstack-project2.onrender.com/api/leffat")
      .then((results) => {
        return results.json();
      })
      .then((data) => {
        console.log(data);
        setResults(data)
      });
  };

  const LeffaArray = (data) => {
     return (
      <>
        <p id="nimi">{data.title}</p>
        {/* eslint-disable-next-line*/}
        <img id="juliste" src={data.poster}/>
      </>
     )
  }

  const YksiLeffa = (data) => {
    console.log("Yksi leffa funktio laukeaa")
    //Tässä pitäisi jotenkin saada poistettua tyhjä tila. Kyseessä ylimääräinen tyhjä img ja p elementti
    const nimi = data.title;
    const juliste = data.poster;
    document.getElementById("yksi").innerHTML=nimi;
    document.getElementById("yksiPoster").src=juliste;
  }

  const ElokuvanLisaaja = (event) => {
    event.preventDefault();
    fetch("https://fullstack-project2.onrender.com/api/lisaa", { method: 'POST', body: JSON.stringify({
      title: nimi,
      year: vuosi
     }),
     headers: {
      'Content-type': 'application/json; charset=UTF-8',
    },
  })
     .then((response) => {
      return response.json();
    })
    .then((data) => {
      console.log(data);
   })
    console.log("Lisätään elokuva " + nimi + " ja " + vuosi);

  }

  return (
    <div>
      <h1>Testing...</h1>
      <p>More testing....</p>
      <button type="button" className="btn" onClick={HaeLeffaData}>
        Hae kaikki leffat
      </button>
      <form onSubmit={ClickHandler}>
        <label>Hae elokuva id:llä</label>
        <input value={query} type="text" onChange={(event) => setQuery(event.target.value)}/>
        <button type="submit">
          Hae yksi leffa
        </button>
      </form>
      <form onSubmit={ElokuvanLisaaja}>
        <label>Anna vuosi</label>
        <input onChange={(event) => setVuosi(event.target.value)} type="text"/>
        <label>Anna nimi</label>
        <input onChange={(event) => setNimi(event.target.value)} type="text"/>
        <button type="submit">
          Luo uusi elokuva
        </button>
      </form>
      {
        results ?
          results.map((data, index) => <LeffaArray title={data.title} poster={data.poster} />)
          : <div> AAAA </div>
        }

        <div id="yksi"></div>
        {/* eslint-disable-next-line*/}
        <img id="yksiPoster" src=""/>
    </div>
  );
}

export default App;
