import React, { useState } from "react";
import './App.css';

function App() {

  const [query, setQuery] = useState();
  const [results, setResults] = useState([]);

  const ClickHandler = (event, props) => {
    event.preventDefault();
    console.log("Toimii");
    setQuery(props.target.value);
    console.log(query);
    //HaeYksiLeffa();
  };
//CORS pitää olla selaimessa sallittu, muuten ei toimi.
  const HaeLeffaData = () => {
    console.log("Haetaan elokuvia...");
    fetch("https://fullstack-project2.onrender.com/api/leffat")
      .then((results) => {
        return results.json();
      })
      .then((data) => {
        console.log(data);
        setResults(data)
      });
  };

  const LeffaArray = (props) => {
     return (
      <>
        <p>{props.title}</p>
        <img alt="juliste" src={props.poster}/>
      </>
     )
  }

  const HaeYksiLeffa = () => {

  }

  return (
    <div>
      <h1>Testing...</h1>
      <p>More testing....</p>
      <button type="button" className="btn" onClick={HaeLeffaData}>
        Hae kaikki leffat
      </button>
      <form onsubmit={ClickHandler}>
        <label>Hae elokuva id:llä</label>
        <input value={query} type="text" />
        <button type="submit">
          Lähetä
        </button>
      </form>
      {results.map((data, index) => <LeffaArray title={data.title} poster={data.poster} />)}
    </div>
  );
}

export default App;
